package org.mzj.test.service.impl;import java.util.ArrayList;import java.util.Arrays;import java.util.HashMap;import java.util.List;import java.util.Map;import java.util.stream.Collectors;import org.mzj.test.model.User;import org.mzj.test.service.UserService;import org.mzjspringframework.beans.annotation.Service;@Servicepublic class UserServiceImpl implements UserService {	private static List<User> users = new ArrayList<User>();	private static Map<Integer, User> idUserMap = new HashMap<Integer, User>();	private static Map<String, Object> nameUsersMap = new HashMap<String,Object>();	static {		users.add(new User(1, "zhang3"));		users.add(new User(2, "li4"));		users.add(new User(3, "li4"));		idUserMap = users.stream().collect(Collectors.toMap(x -> x.getId(), x -> x));		nameUsersMap = users.stream().collect(Collectors.toMap(User::getName, user -> Arrays.asList(user), (key1, key2) -> Arrays.asList(key1,key2)));	}	@Override	public User getUserById(int id) {		return idUserMap.get(id);	}	@Override	public List<User> getUsersByName(String name) {		return (List<User>) nameUsersMap.get(name);	}}